const c=r=>new Promise(e=>setTimeout(e,r));async function o(){try{await c(800);const r=await fetch("/products.json");if(!r.ok)throw new Error("Erreur de chargement des produits");return(await r.json()).products}catch(r){return console.error("Erreur lors du chargement des produits:",r),[{id:"1",name:"Collier Éclat Lunaire",category:"Colliers",reference:"CL-001",price:2450,currency:"XOF",description:"Un collier élégant avec pierres fines et argent 925.",badges:["Nouveauté","Exclusif"]},{id:"2",name:"Bracelet Solaire",category:"Bracelets",reference:"BR-002",price:1800,currency:"XOF",description:"Bracelet en or jaune 18 carats avec diamants.",badges:["Best-seller"]}]}}async function a(r){try{return(await o()).find(t=>t.id===parseInt(r))}catch(e){return console.error("Erreur lors du chargement du produit:",e),null}}export{a,o as f};
